const NO_OF_CHARS = 256;

function badCharHeuristic(string, size) {
  const badChar = new Array(NO_OF_CHARS).fill(-1);
  for (let i = 0; i < size; i++) {
    badChar[string.charCodeAt(i)] = i;
  }
  return badChar;
}

function search(txt, pat) {
  const m = pat.length;
  const n = txt.length;
  const badChar = badCharHeuristic(pat, m);
  let s = 0;
  while (s <= n - m) {
    let j = m - 1;
    while (j >= 0 && pat[j] === txt[s + j]) {
      j--;
    }
    if (j < 0) {
      console.log("Pattern occurs at shift = " + s);
      s += (s + m < n) ? m - badChar[txt.charCodeAt(s + m)] : 1;
    } else {
      s += Math.max(1, j - badChar[txt.charCodeAt(s + j)]);
    }
  }
}
