def BellmanFord(graph, V, E, src):
    dis = [sys.maxsize] * V
    dis[src] = 0
    for i in range(V - 1):
        for j in range(E):
            if dis[graph[j][0]] != sys.maxsize and dis[graph[j][0]] + graph[j][2] < dis[graph[j][1]]:
                dis[graph[j][1]] = dis[graph[j][0]] + graph[j][2]
    for i in range(E):
        x = graph[i][0]
        y = graph[i][1]
        weight = graph[i][2]
        if dis[x] != sys.maxsize and dis[x] + weight < dis[y]:
            print("Graph contains negative weight cycle")
