```javascript
class Node {
    constructor(feature = null, threshold = null, left = null, right = null, value = null) {
        this.feature = feature;
        this.threshold = threshold;
        this.left = left;
        this.right = right;
        this.value = value;
    }

    isLeafNode() {
        return this.value !== null;
    }
}

class DecisionTree {
    constructor(minSamplesSplit = 2, maxDepth = 100, nFeatures = null) {
        this.minSamplesSplit = minSamplesSplit;
        this.maxDepth = maxDepth;
        this.nFeatures = nFeatures;
        this.root = null;
    }

    fit(X, y) {
        this.nFeatures = this.nFeatures ? Math.min(X[0].length, this.nFeatures) : X[0].length;
        this.root = this._growTree(X, y);
    }

    _growTree(X, y, depth = 0) {
        const nSamples = X.length;
        const nFeats = X[0].length;
        const nLabels = new Set(y).size;

        if (depth >= this.maxDepth || nLabels === 1 || nSamples < this.minSamplesSplit) {
            const leafValue = this._mostCommonLabel(y);
            return new Node(null, null, null, null, leafValue);
        }

        const featIdxs = this._sampleFeatures(nFeats);
        const [bestFeature, bestThresh] = this._bestSplit(X, y, featIdxs);
        const [leftIdxs, rightIdxs] = this._split(X, bestFeature, bestThresh);
        const left = this._growTree(this._getSubset(X, leftIdxs), y.filter((_, i) => leftIdxs.includes(i)), depth + 1);
        const right = this._growTree(this._getSubset(X, rightIdxs), y.filter((_, i) => rightIdxs.includes(i)), depth + 1);
        return new Node(bestFeature, bestThresh, left, right);
    }

    _bestSplit(X, y, featIdxs) {
        let bestGain = -1;
        let splitIdx, splitThreshold;
        for (let i = 0; i < featIdxs.length; i++) {
            const featIdx = featIdxs[i];
            const XColumn = X.map(row => row[featIdx]);
            const thresholds = new Set(XColumn);
            for (const thr of thresholds) {
                const gain = this._informationGain(y, XColumn, thr);
                if (gain > bestGain) {
                    bestGain = gain;
                    splitIdx = featIdx;
                    splitThreshold = thr;
                }
            }
        }
        return [splitIdx, splitThreshold];
    }

    _informationGain(y, XColumn, threshold) {
        const parentEntropy = this._entropy(y);
        const [leftIdxs, rightIdxs] = this._split(XColumn, null, threshold);
        if (leftIdxs.length === 0 || rightIdxs.length === 0) {
            return 0;
        }
        const n = y.length;
        const nL = leftIdxs.length;
        const nR = rightIdxs.length;
        const eL = this._entropy(y.filter((_, i) => leftIdxs.includes(i)));
        const eR = this._entropy(y.filter((_, i) => rightIdxs.includes(i)));
        const childEntropy = (nL / n) * eL + (nR / n) * eR;
        const informationGain = parentEntropy - childEntropy;
        return informationGain;
    }

    _split(X, feature, threshold) {
        const leftIdxs = [];
        const rightIdxs = [];
        for (let i = 0; i < X.length; i++) {
            if (feature !== null) {
                if (X[i][feature] <= threshold) {
                    leftIdxs.push(i);
                } else {
                    rightIdxs.push(i);
                }
            } else {
                if (X[i] <= threshold) {
                    leftIdxs.push(i);
                } else {
                    rightIdxs.push(i);
                }
            }
        }
        return [leftIdxs, rightIdxs];
    }

    _entropy(y) {
        const counts = {};
        for (const label of y) {
            counts[label] = (counts[label] || 0) + 1;
        }
        const ps = Object.values(counts).map(count => count / y.length);
        return -ps.reduce((sum, p) => p > 0 ? sum + p * Math.log2(p) : sum, 0);
    }

    _mostCommonLabel(y) {
        const counts = {};
        let mostCommonLabel = null;
        let maxCount = 0;
        for (const label of y) {
            counts[label] = (counts[label] || 0) + 1;
            if (counts[label] > maxCount) {
                mostCommonLabel = label;
                maxCount = counts[label];
            }
        }
        return mostCommonLabel;
    }

    _sampleFeatures(nFeats) {
        const featIdxs = [];
        while (featIdxs.length < this.nFeatures) {
            const randomIdx = Math.floor(Math.random() * nFeats);
            if (!featIdxs.includes(randomIdx)) {
                featIdxs.push(randomIdx);
            }
        }
        return featIdxs;
    }

    _getSubset(X, idxs) {
        const subset = [];
        for (const idx of idxs) {
            subset.push(X[idx]);
        }
        return subset;
    }

    predict(X) {
        const predictions = [];
        for (const x of X) {
            predictions.push(this._traverseTree(x, this.root));
        }
        return predictions;
    }

    _traverseTree(x, node) {
        if (node.isLeafNode()) {
            return node.value;
        }
        if (x[node.feature] <= node.threshold) {
            return this._traverseTree(x, node.left);
        }
        return this._traverseTree(x, node.right);
    }
}
