class Node {
  constructor(char, freq) {
    this.char = char;
    this.freq = freq;
    this.left = null;
    this.right = null;
  }
}

function buildHuffmanTree(text) {
  const freq = {};
  for (let char of text) {
    freq[char] = (freq[char] || 0) + 1;
  }
  const heap = Object.entries(freq).map(([char, f]) => new Node(char, f));
  heap.sort((a, b) => a.freq - b.freq);
  while (heap.length > 1) {
    const left = heap.shift();
    const right = heap.shift();
    const parent = new Node(null, left.freq + right.freq);
    parent.left = left;
    parent.right = right;
    let i = 0;
    while (i < heap.length && parent.freq > heap[i].freq) {
      i++;
    }
    heap.splice(i, 0, parent);
  }
  return heap[0];
}

function buildHuffmanCodes(root, code = '', codes = {}) {
  if (!root) return;
  if (root.char) {
    codes[root.char] = code;
    return;
  }
  buildHuffmanCodes(root.left, code + '0', codes);
  buildHuffmanCodes(root.right, code + '1', codes);
  return codes;
}
