class Node {
  constructor(position, parent = null) {
    this.position = position;
    this.parent = parent;
    this.g = 0;
    this.h = 0;
    this.f = 0;
  }
}

function astar(maze, start, end) {
  const openList = [];
  const closedList = [];
  const startNode = new Node(start);
  const endNode = new Node(end);
  openList.push(startNode);
  while (openList.length > 0) {
    openList.sort((a, b) => a.f - b.f);
    const currentNode = openList.shift();
    closedList.push(currentNode);
    if (currentNode.position[0] === endNode.position[0] && currentNode.position[1] === endNode.position[1]) {
      const path = [];
      let current = currentNode;
      while (current !== null) {
        path.push(current.position);
        current = current.parent;
      }
      return path.reverse();
    }
    const children = [];
    for (let newPosition of [[0, -1], [0, 1], [-1, 0], [1, 0]]) {
      const nodePosition = [currentNode.position[0] + newPosition[0], currentNode.position[1] + newPosition[1]];
      if (nodePosition[0] > (maze.length - 1) || nodePosition[0] < 0 || nodePosition[1] > (maze[0].length - 1) || nodePosition[1] < 0) {
        continue;
      }
      if (maze[nodePosition[0]][nodePosition[1]] !== 0) {
        continue;
      }
      const newNode = new Node(nodePosition, currentNode);
      children.push(newNode);
    }
    for (let child of children) {
      if (closedList.some(node => node.position[0] === child.position[0] && node.position[1] === child.position[1])) {
        continue;
      }
      child.g = currentNode.g + 1;
      child.h = Math.pow(child.position[0] - endNode.position[0], 2) + Math.pow(child.position[1] - endNode.position[1], 2);
      child.f = child.g + child.h;
      if (!openList.some(node => node.position[0] === child.position[0] && node.position[1] === child.position[1] && child.g > node.g)) {
        openList.push(child);
      }
    }
  }
  return [];
}
